/* Navbar.css */
.navbar {
  display: flex;
  align-items: center;
  justify-content: space-between;
  background-color: #232f3e;
  color: white;
  padding: 10px 20px;
}

.navbar__logoImage {
  height: 40px;
}

.navbar__search {
  display: flex;
  flex: 1;
  margin: 0 20px;
}

.navbar__searchInput {
  width: 100%;
  padding: 8px;
  border: none;
  border-radius: 4px 0 0 4px;
  outline: none;
}

.navbar__searchButton {
  padding: 8px 15px;
  background-color: #febd69;
  border: none;
  border-radius: 0 4px 4px 0;
  cursor: pointer;
}

.navbar__links {
  display: flex;
  gap: 20px;
  align-items: center;
}

.navbar__link {
  color: white;
  text-decoration: none;
  font-size: 16px;
  transition: color 0.3s;
}

.navbar__link:hover {
  color: #febd69;
}

.navbar__cart {
  position: relative;
}

.navbar__cartIcon {
  width: 45px;
  height: 30px;
}

.navbar__cartCount {
  position: absolute;
  top: -5px;
  right: -5px;
  background-color: red;
  color: white;
  border-radius: 50%;
  width: 18px;
  height: 18px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 12px;
}
