commits.content:
.productCard {
  border: 1px solid #ccc;
  padding: 15px;
  margin-bottom: 10px;
  border-radius: 8px;
}

.addToCartBtn {
  margin-right: 10px;
  background-color: #4CAF50;
  color: white;
  border: none;
  padding: 8px 12px;
  cursor: pointer;
}

.deleteBtn {
  background-color: #f44336;
  color: white;
  border: none;
  padding: 8px 12px;
  cursor: pointer;
}

.addToCartBtn:hover {
  background-color: #45a049;
}

.deleteBtn:hover {
  background-color: #d32f2f;
}
