.user-card {
  border: 1px solid #e0e0e0;
  border-radius: 8px;
  padding: 16px;
  margin-bottom: 16px;
  background-color: #f9f9f9;
  transition: box-shadow 0.3s ease;
}

.user-card:hover {
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.1);
}

.user-card h3 {
  font-size: 18px;
  margin-bottom: 8px;
  color: #333;
}

.user-card p {
  margin: 4px 0;
  color: #555;
}
