/* index.css */
body {
  margin: 0;
  padding: 0;
  overflow-x: hidden;
}

#root {
  margin: 0;
  padding: 0;
  width: 100%;
}

header {
  background-color: #131921;
  padding: 10px;
  color: white;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.navbar {
  display: flex;
  justify-content: space-around;
  width: 100%;
}

.navbar__link {
  color: white;
  text-decoration: none;
}

.banner {
  position: relative;
  margin-top: 20px;
  text-align: center;
}

.banner__image {
  width: 100%;
  height: auto;
}

.banner__info {
  position: absolute;
  top: 20%;
  left: 0;
  right: 0;
  color: white;
  font-size: 30px;
}

.product-list {
  margin: 20px;
}

.product-list__grid {
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 20px;
}

.product-card {
  padding: 15px;
  border: 1px solid #ddd;
  border-radius: 5px;
  text-align: center;
}

.product-card img {
  width: 100%;
  height: 200px;
  object-fit: cover;
}

footer {
  background-color: #131921;
  color: white;
  text-align: center;
  padding: 10px;
  margin-top: 20px;
}
